import React, { Component } from "react";

import { getTopics } from "../services/fakeTopicService";
import { getCounters } from "../services/fakeCounterService";
import "../index.css";
import NameComponent from "./nameComponent";
import CounterComponent from "./counterComponent";

class ContentComponent extends React.Component {
  state = { topics: [], counters: [] };

  componentDidMount() {
    const topics = getTopics();
    const counters = getCounters();

    this.setState({ topics, counters });
  }

  getCounter(_id) {
    let counter = this.state.counters.find((counter) => counter._id === _id);
    if (counter.counter > 0) {
      return <CounterComponent counter={counter.counter} />;
    }
  }

  getName(_id) {
    let topic = this.state.topics.find((topic) => topic._id === _id);
    return topic.name;
  }

  getContentComponent(counter) {
    let nameComponent = null;
    let counterComponent = null;

    if (counter.counter === 0) {
      return null;
    }

    return <div key={counter._id} className="contentComponent"></div>;

    // <div key={counter._id} className="contentComponent"></div>;
  }

  render() {
    return (
      <div className="contentContainer">
        {this.state.counters.map((counter) =>
          this.getContentComponent(counter)
        )}

        {/* {this.state.topics.map((topic) => (
          <div key={topic._id} className="contentComponent">
            <NameComponent key={topic._id} name={topic.name} />
            {this.getCounter(topic._id)}
            <CounterComponent counter={this.getCounter(topic._id)} />
          </div>
        ))} */}
      </div>
    );
  }
}

export default ContentComponent;
